//   for (let i = 0; i < rawLength; i++) {
//     array[i] = raw.charCodeAt(i);
//   }
//   return array;
// }

// bliver kaldt først
function uploadImage(topic: string, files: FileList, client: MqttClient) {
  for (let i = 0; i < files.length; i++) {
    const file = files.item(i);
    if (file != null) {
      const base64File = upload(file);
      console.log("this is the base64: " + base64File);
      client.publish("RainbowDash/" + topic, base64File);
      console.log("publishing file to topic: " + topic);
    }
  }
}

function fileToByteArray(file: any) {
  return new Promise((resolve, reject) => {
    try {
      let reader = new FileReader();
      let fileByteArray: any = [];
      reader.readAsArrayBuffer(file);
      reader.onloadend = (evt) => {
        if (evt.target != null) {
          if (evt.target.result != null) {
            if (evt.target.readyState === FileReader.DONE) {
              let arrayBuffer = evt.target.result,
                array = new Uint8Array(arrayBuffer as ArrayBuffer);
              for (byte of array) {
                fileByteArray.push(byte);
              }
            }
          }
        }
        resolve(fileByteArray);
      }
    }
    catch (e) {
      reject(e);
    }
  })
}



